'use strict';

/* Harshad Number
 * Function: isHarshad()
 */

var validate = require('../validation/positive-integer');

function isHarshad(num) {
	var sum = 0;
	for (var i = num; i > 0; i = parseInt(i / 10, 10)) {
		sum += i % 10;
	}
	return num % sum === 0;
}
exports.check = function (num) {
	validate(num, 'isHarshad');
	return isHarshad(num);
};