'use strict';

/* Perfect Number
 * Function: isPerfect()
 */

var validate = require('../validation/positive-integer');

exports.check = function (num) {
    validate(num, 'isPerfect');
    var count = 0;
    for (var i = 1; i < num; i++) {
        if (num % i === 0) {
            count = count + i;
        }
    }

    return count == num ? true : false;
};