'use strict';

/* Function: sort() */

var validate = require('../validation/number-array');

function validateSecondArg(arg, order) {
	if (typeof order != 'boolean') throw new TypeError('Invalid argument received: ' + JSON.stringify(arg) + ', ' + order + '\n\'sort()\' only accept an array of real numbers and boolean <optional> as the arguments!\n');
}

module.exports = function (arr) {
	var ascend = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : true;

	validate(arr, 'sort');
	validateSecondArg(arr, ascend);
	return arr.sort(function (prev, next) {
		return ascend ? prev - next : next - prev;
	});
};